const Server = require('karma').Server;
const path = require('path');

module.exports = () => (done) => {
	const configPath = path.resolve('./karma.conf.js');
	const karmaServer = new Server({
		configFile: configPath,
	}, () => {
		console.log('You can find the details of the code coverage in reports/coverage/index.html');
		done();
	});

	karmaServer.start();
};

