#!/usr/bin/env bash

# purpose: create a rev.txt for

branch=`git rev-parse --abbrev-ref HEAD`
commit=`git rev-parse HEAD`
commit_short=`git rev-parse --short=7 HEAD`
builder="Jenkins on `hostname`"
last_tag=`git describe --tags --abbrev=0`
tag_relation=`git describe --tags --abbrev=1`
echo "Date: `date`" > rev.txt
echo "Path: $branch" >> rev.txt
echo "Revision: $commit" >> rev.txt
echo "Built by: $builder" >> rev.txt
echo "Last Tag: $last_tag" >> rev.txt
echo "Current Tag (automated): $tag_relation" >> rev.txt
