const faker = require('faker');

const singleImage = () => {
	const imageSrc = Math.floor(Math.random() * ((3 - 1) + 1)) + 1;
	const image = {
		title: faker.commerce.productName(),
		description: faker.lorem.paragraphs(),
		image: {
			srcset: [
				{
					src: `/img/content/gallery/gallery_${imageSrc}.jpg`,
					width: '1x',
				},
				{
					src: `/img/content/gallery/gallery_${imageSrc}.jpg`,
					width: '2x',
				},
			],
			src: `/img/content/gallery/gallery_${imageSrc}.jpg`,
			srcsetString: `/img/content/gallery/gallery_${imageSrc}.jpg 1x, /img/content/gallery/gallery_${imageSrc}.jpg 2x`,
			alt: faker.commerce.productName(),
			sizes: '',
		},
	};

	return image;
};

const galleryImages = numImages => Array.from({ length: numImages }, singleImage);

const gallery = galleryImages(8);

module.exports = gallery;
