const faker = require('faker');

const multiplyItems = (qtyItems, item) =>
	Array.from({ length: qtyItems }, () => item);

const subCategory = {
	title: faker.commerce.productName(),
	url: `/components/preview/t-category-l3?cat=${faker.commerce.productName()}`,
};

const category = {
	title: faker.commerce.department(),
	url: `/components/preview/t-category-l2?cat=${faker.commerce.productName()}`,
	items: multiplyItems(20, subCategory),
};

const categories = {
	title: 'Sortiment',
	url: '#sortiment',
	isActive: true,
	items: multiplyItems(10, category),
};

module.exports = categories;
