const modifiers = [
	'xx-small', 'x-small', 'small', 'large', 'larger', 'x-large',
	'huge', 'x-huge', 'stroked', 'stroked-thin', 'stroked-bold',
	'rounded-thin', 'rounded', 'filled', 'naked', 'fill-teal',
	'fill-teal-dark', 'fill-teal-light', 'block',
];

const variants = modifiers.map(modifier => (
	{
		name: modifier,
		context: {
			modifiers: [modifier],
		},
	}
));

module.exports = {
	title: 'SVG Icon',
	name: 'svg-icon',
	context: {
		data: {
			name: 'account',
		},
	},
	variants,
};
