import ResiroopCarousel from '../carousel-resiroop/carousel.js';

const SELECTORS = {
	carousel: '.js-o-banner__carousel',
	items: '.js-o-banner__carousel-items',
};

const optionsMainCarousel = el => ({
	selector: el,
	arrows: true,
	lazyload: true,
	pagination: true,
	loop: true,
	draggable: true,
	duration: 300,
});

class Banner {
	constructor($el) {
		this.el = $el.get(0);
		this.mainCarouselItems = this.el.querySelector(SELECTORS.items);
		this.mainCarouselInstance = new ResiroopCarousel(optionsMainCarousel(this.mainCarouselItems));
	}
}

export default Banner;
