const productsData = require('../../../00-data/product/');

productsData.map((element, index) => {
	const item = element;
	item.images[0].alt = element.title;
	item.cartItems = index + 1;
	item.cartItemsLabel = (element.cartItems > 1) ? 'Stücke' : 'Stück';
	item.stockStatus = 'zur Zeit nicht verfügbar';
	return item;
});

const products = productsData.slice(0, 10);

module.exports = {
	title: 'Cart Product list',
	name: 'cart-product-list',
	context: {
		modifier: '',
		data: {
			products,
			remove: 'Produkt entfernen',
		},
	},
	variants: [
		{
			name: 'read-only',
			context: {
				modifier: 'read-only',
				data: {
					products,
					remove: 'Produkt entfernen',
					readOnly: true,
				},
			},
		},
	],
};
