
const selectors = {
	checkboxList: '.o-checkbox-list',
	checkboxListItem: '.m-form-field-checkbox__input',
};

function onCheckboxToggle(e) {
	const $checkbox = $(e.currentTarget);
	$checkbox.attr('checked', !$checkbox.attr('checked'));
}

class CheckboxList {
	constructor($el) {
		this.$el = $el;
		this.$checkboxList = this.$el.find(selectors.checkboxList);
		this.$checkboxItems = this.$el.find(selectors.checkboxListItem);
		this.$checkboxItems.on('click', onCheckboxToggle);
	}
}

export default CheckboxList;
