const singleProduct = require('../../../00-data/product/');

const productImages = singleProduct[0].images;

const mappedImages = productImages.map(item => ({
	caption: item.title,
	image: {
		src: item.src,
		srcset: item.srcset,
		alt: item.alt,
		srcsetString: item.srcsetString,
	},
}));

module.exports = {
	title: 'Gallery',
	name: 'gallery',
	context: {
		data: {
			items: [...mappedImages, ...mappedImages],
			labelNext: 'Next',
			labelPrev: 'Prev',
		},
	},
};

