import ResiroopCarousel from '../carousel-resiroop/carousel.js';

const SELECTORS = {
	carousel: '.js-o-gallery',
	mainCarousel: '.js-o-gallery__main-carousel',
	items: '.js-o-gallery__items',
};

const optionsMainCarousel = el => ({
	selector: el,
	arrows: true,
	pagination: true,
	loop: true,
	lazyload: true,
	draggable: true,
	duration: 300,

	thumbnails: {
		perPage: 5,
		draggable: false,
		loop: true,
		arrows: true,
	},
});
class Gallery {
	constructor($el) {
		this.el = $el.get(0);
		this.mainCarouselItems = this.el.querySelector(SELECTORS.items);
		this.mainCarouselInstance = new ResiroopCarousel(optionsMainCarousel(this.mainCarouselItems));
	}
}

export default Gallery;
