import A11yDialog from 'a11y-dialog';


const selectors = {
	content: '[data-a11y-dialog-show]',
	modalWindow: '.js-o-modal-window__container',
};

class ModalWindow {
	constructor($el) {
		const el = $el.get(0); // no jquery on purpose
		const modalWindow = el.querySelector(selectors.modalWindow);
		const content = document.querySelector(selectors.content);
		this.dialog = new A11yDialog(modalWindow, content);
	}
}

export default ModalWindow;
