const productsData = require('../../../00-data/product/');

productsData.map((element, index) => {
	const item = element;
	item.images[0].alt = element.title;
	item.wishtlistItems = index + 1;
	item.wishtlistItemsLabel = (element.wishtlistItems > 1) ? 'Stücke' : 'Stück';
	item.stockStatus = 'zur Zeit nicht verfügbar';
	return item;
});

const products = productsData.slice(0, 10);

module.exports = {
	title: 'Wishlist list',
	name: 'wishlist-list',
	context: {
		modifier: '',
		data: {
			products,
			remove: 'Produkt entfernen',
			addToCart: 'Add to cart',
		},
	},
};
